package com.training.second;import java.io.Serializable;import java.util.Objects;public class Student extends Person implements Serializable{	public int rollNumber;	String course;	String specilization;	 int marks;	String address;public Student() {	rollNumber=77;	course="Bachelor of arts";	specilization="Criminology and police administration";	marks=97;	address="DGVC,Arumbakkam,chennai";}public Student(String personName,String personAddress,long personMbno,int rollNumber,String studentCourse,String specName,int studentMarks,String studentAdd) {	super(personName,personAddress,personMbno);	this.rollNumber=rollNumber;	course=studentCourse;	specilization=specName;	marks=studentMarks;	address=studentAdd;	}public int getRollNumber() {	return rollNumber;}public String getCourse() {	return course;}public  float getMarks() {	return marks;}@Overridepublic String toString() {	return "Student [rollNumber=" + rollNumber + ", course=" + course + ", specilization=" + specilization			+ ", address=" + address + "marks"+marks;}@Override	public int hashCode() {		return Objects.hash(address, course, rollNumber, specilization);	}	@Override	public boolean equals(Object obj) {		if (this == obj)			return true;		if (obj == null)			return false;		if (getClass() != obj.getClass())			return false;		Student other = (Student) obj;		return Objects.equals(address, other.address) && Objects.equals(course, other.course)				&& rollNumber == other.rollNumber && Objects.equals(specilization, other.specilization);	}public String display() {	super.transactionDetails();	return "rollNumber is :"+rollNumber+"course is:"+course+"specilization is :"+specilization+"marks is :"+marks			+"address is :"+address;}public String display(int studentRno,		String studentCourse,String specName,int studentMarks,String studentAdd){	super.transactionDetails();	this.rollNumber=studentRno;	this.course=studentCourse;	this.specilization=specName;	this.marks=studentMarks;	this.address=studentAdd;	return "rollNumber is :"+studentRno+"course is:"+studentCourse+"specilization is :"+specName+"marks is :"+studentMarks			+"address is :"+studentAdd;}public void transactionDetails() {	super.transactionDetails();	System.out.println("Name is :"+name+"Address is :"+address+"Phone is :"+phno);}}